$(document).ready(function(){var e=parseInt(document.getElementById("wins").innerHTML),n=parseInt(document.getElementById("losses").innerHTML);new CanvasJS.Chart("chartcontainer",{animationEnabled:!0,data:[{type:"pie",startAngle:0,yValueFormatString:'##0.00"%"',indexLabel:"{label} {y}",dataPoints:[{y:e/(n+e)*100,label:"Wins"},{y:n/(n+e)*100,label:"Losses"}]}]}).render()});